mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime},
where id = #{id}

update student

    violation_count = violation_count + 1,
    violation_score = violation_score + #{score}

where id = #{id}

mapper.select:
select s.*, c.name clazz_name from student s left join clazz c on s.clazz_id = c.id

    and s.name like concat('%',#{name},'%')
    and degree = #{degree}
    and clazz_id = #{clazzId}
